HTMLFormElement.prototype.serialize=function(){var g=Array.prototype.slice;var a=g.call(this.getElementsByTagName("input"));var c=g.call(this.getElementsByTagName("select"));while(c.length){a.push(c.pop())}var b={};for(var d=0;d<a.length;d++){b[a[d].name]=a[d].value}var e=[];for(d in b){e.push(d+"="+b[d])}return e.join("&")};
